insert into products (id, category_id, name, description, price, image, rating, status, stock) values (16, 2, 'Pack comedero + pelota + juguete dental', 'Incluye un comedero de metal, una pelota de caucho y un juguete para que tu cachorro pueda morder sin culpa!', 2261.83, 'producto16.jpg', 4, 'oferta', 4);
